exports.graph = function(coordinates) {

    let benchmark = {};
    benchmark.startTime = Date.now();

    // --- Properties
    this.nodes = coordinates;
    this.nodeCount = coordinates.length;
    this.adjacencyMatrix = [];
    this.edges = []; 
    this.edgeCount = 0;
    this.maxEdge = 0;
    this.minEdge = 1000;
    this.sumEdge = 0;
    this.mstEdgeList = [];    
    this.clusterEdges = [];        

    // --- Creates empty adjacency matrix
    for(let i = 0; i <  this.nodeCount; i++) this.adjacencyMatrix.push([]);

    // --- For each pair of nodes v1 and v2
    for(let v1 = 0; v1 <  this.nodeCount; v1++){
        for(let v2 = v1 + 1; v2 <  this.nodeCount; v2++){
            
            // --- Calculates euclidian distance between nodes 
            let dx = this.nodes[v1][0] - this.nodes[v2][0];             // --- x axis component
            let dy = this.nodes[v1][1] - this.nodes[v2][1];             // --- y axis component
            let d = Math.sqrt(dx*dx + dy*dy);                           // --- distance
            
            // --- Stores min and max edge sizes, edge sum and edge count, for information and debug...
            if(d > this.maxEdge) this.maxEdge = d;
            else if(d < this.minEdge) this.minEdge = d;
            this.sumEdge += d;
            this.edgeCount++;

            // --- Inserts edges value into the adjacency matrix
            this.adjacencyMatrix[v1][v2] = d;
            this.adjacencyMatrix[v2][v1] = d;

            // --- Creates edges array
            this.edges.push({ 
                v1: v1,
                v2: v2,
                d: d                        
            });
        }
    }

    // --- 
    benchmark.endTime = Date.now();
    benchmark.interval = benchmark.endTime - benchmark.startTime;
    console.log("--- Graph Setup Finished in", benchmark.interval, "ms");    
    console.log("    Node Count:", this.nodeCount);            
    console.log("    Edge Count:", this.edgeCount);        
    console.log("    Max Edge:", this.maxEdge);  
    console.log("    Min Edge:", this.minEdge);          
    console.log("");           
}
