exports.unionFind = function (arraySize) {
    this.id = [];
    this.size = [];

    for(i = 0; i < arraySize; i++){
        this.id[i] = i;
        this.size[i] = 1;    
    }

    this.union = function(x, y){
        let rX = this.find(x);
        let rY = this.find(y);
        
        if(rX != rY){
            if(this.size[rX] < this.size[rY]){
                this.size[rY] += this.size[rX];
                this.id[rX] = rY;
            }else{
                this.size[rX] += this.size[rY];
                this.id[rY] = rX;
            }
        }
    }
    
    this.find = function(x){
        while(x != this.id[x]){
            this.id[x] = this.id[this.id[x]];
            x = this.id[x];
        }
    
        return x;
    }
};
